import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { extractTasksFromNote } from "./services/ai-service";
import { featureFlagService } from "./services/feature-flags-service";
import { 
  insertProjectSchema, 
  insertNoteSchema, 
  insertTaskSchema,
  insertTimeEntrySchema,
  insertActiveTimerSchema 
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Projects
  app.get("/api/projects", async (req, res) => {
    try {
      const projects = await storage.getProjects();
      res.json(projects);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch projects" });
    }
  });

  app.post("/api/projects", async (req, res) => {
    try {
      const validation = insertProjectSchema.safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid project data", errors: validation.error.errors });
      }

      const project = await storage.createProject(validation.data);
      res.status(201).json(project);
    } catch (error) {
      res.status(500).json({ message: "Failed to create project" });
    }
  });

  app.get("/api/projects/:id", async (req, res) => {
    try {
      const project = await storage.getProject(req.params.id);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.json(project);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch project" });
    }
  });

  app.patch("/api/projects/:id", async (req, res) => {
    try {
      const validation = insertProjectSchema.partial().safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid project data", errors: validation.error.errors });
      }

      const project = await storage.updateProject(req.params.id, validation.data);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.json(project);
    } catch (error) {
      res.status(500).json({ message: "Failed to update project" });
    }
  });

  app.delete("/api/projects/:id", async (req, res) => {
    try {
      const success = await storage.deleteProject(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete project" });
    }
  });

  // Notes
  app.get("/api/projects/:projectId/notes", async (req, res) => {
    try {
      const notes = await storage.getNotesByProject(req.params.projectId);
      res.json(notes);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch notes" });
    }
  });

  app.post("/api/projects/:projectId/notes", async (req, res) => {
    try {
      const noteData = { ...req.body, projectId: req.params.projectId };
      const validation = insertNoteSchema.safeParse(noteData);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid note data", errors: validation.error.errors });
      }

      const note = await storage.createNote(validation.data);
      res.status(201).json(note);
    } catch (error) {
      res.status(500).json({ message: "Failed to create note" });
    }
  });

  app.get("/api/notes/:id", async (req, res) => {
    try {
      const note = await storage.getNote(req.params.id);
      if (!note) {
        return res.status(404).json({ message: "Note not found" });
      }
      res.json(note);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch note" });
    }
  });

  app.patch("/api/notes/:id", async (req, res) => {
    try {
      const validation = insertNoteSchema.partial().safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid note data", errors: validation.error.errors });
      }

      const note = await storage.updateNote(req.params.id, validation.data);
      if (!note) {
        return res.status(404).json({ message: "Note not found" });
      }

      // Auto-extract tasks from new content
      if (validation.data.content) {
        const oldNote = await storage.getNote(req.params.id);
        if (oldNote) {
          const oldLength = oldNote.lastProcessedLength || 0;
          const newContent = validation.data.content;

          if (newContent.length > oldLength) {
            // Extract from new content only
            const newContentPart = newContent.substring(oldLength);

            try {
              const extractResponse = await fetch(`http://localhost:5000/api/notes/${req.params.id}/extract-tasks`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ contentPart: newContentPart })
              });

              if (extractResponse.ok) {
                // Update the processed length
                await storage.updateNote(req.params.id, { 
                  lastProcessedLength: newContent.length 
                });
              }
            } catch (error) {
              console.log('Task extraction failed:', error);
              // Continue even if extraction fails
            }
          }
        }
      }

      res.json(note);
    } catch (error) {
      res.status(500).json({ message: "Failed to update note" });
    }
  });

  app.delete("/api/notes/:id", async (req, res) => {
    try {
      const success = await storage.deleteNote(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Note not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete note" });
    }
  });

  // AI Task Extraction
  app.post("/api/notes/:id/extract-tasks", async (req, res) => {
    try {
      const note = await storage.getNote(req.params.id);
      if (!note) {
        return res.status(404).json({ message: "Note not found" });
      }

      // Use contentPart if provided (for incremental extraction), otherwise use full content
      const contentToProcess = req.body.contentPart || note.content;

      const extractedTasks = await extractTasksFromNote(contentToProcess);
      const createdTasks = [];

      for (const taskData of extractedTasks) {
        const task = await storage.createTask({
          projectId: note.projectId,
          noteId: note.id,
          title: taskData.title,
          description: taskData.description,
          priority: taskData.priority,
          status: 'todo',
          dueDate: taskData.dueDate ? new Date(taskData.dueDate) : undefined,
        });
        createdTasks.push(task);
      }

      res.json({ tasks: createdTasks, count: createdTasks.length });
    } catch (error) {
      console.error("Task extraction error:", error);
      res.status(500).json({ message: "Failed to extract tasks from note" });
    }
  });

  app.post("/api/notes/:id/suggestions", async (req, res) => {
    try {
      const note = await storage.getNote(req.params.id);
      if (!note) {
        return res.status(404).json({ message: "Note not found" });
      }

      const suggestions = await extractTasksFromNote(note.content, note.title);
      res.json({ suggestions });
    } catch (error) {
      console.error("Task suggestions error:", error);
      res.status(500).json({ message: "Failed to generate task suggestions" });
    }
  });

  // Tasks
  app.get("/api/projects/:projectId/tasks", async (req, res) => {
    try {
      const tasks = await storage.getTasksByProject(req.params.projectId);
      res.json(tasks);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch tasks" });
    }
  });

  app.post("/api/projects/:projectId/tasks", async (req, res) => {
    try {
      const taskData = { ...req.body, projectId: req.params.projectId };
      const validation = insertTaskSchema.safeParse(taskData);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid task data", errors: validation.error.errors });
      }

      const task = await storage.createTask(validation.data);
      res.status(201).json(task);
    } catch (error) {
      res.status(500).json({ message: "Failed to create task" });
    }
  });

  app.patch("/api/tasks/:id", async (req, res) => {
    try {
      const validation = insertTaskSchema.partial().safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid task data", errors: validation.error.errors });
      }

      const task = await storage.updateTask(req.params.id, validation.data);
      if (!task) {
        return res.status(404).json({ message: "Task not found" });
      }
      res.json(task);
    } catch (error) {
      res.status(500).json({ message: "Failed to update task" });
    }
  });

  app.delete("/api/tasks/:id", async (req, res) => {
    try {
      const success = await storage.deleteTask(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Task not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete task" });
    }
  });

  // Time Tracking
  app.get("/api/timer/active", async (req, res) => {
    try {
      const activeTimer = await storage.getActiveTimer();
      res.json(activeTimer || null);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch active timer" });
    }
  });

  app.get("/api/timers/active", async (req, res) => {
    try {
      const activeTimers = await storage.getActiveTimers();
      res.json(activeTimers);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch active timers" });
    }
  });

  app.post("/api/timer/start", async (req, res) => {
    try {
      // Transform string date to Date object for validation
      const requestData = {
        ...req.body,
        startTime: req.body.startTime ? new Date(req.body.startTime) : new Date()
      };

      const validation = insertActiveTimerSchema.safeParse(requestData);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid timer data", errors: validation.error.errors });
      }

      const timer = await storage.startTimer(validation.data);
      res.status(201).json(timer);
    } catch (error) {
      res.status(500).json({ message: "Failed to start timer" });
    }
  });

  app.post("/api/timer/stop", async (req, res) => {
    try {
      const { taskId } = req.body;
      const timeEntry = await storage.stopTimer(taskId);
      if (!timeEntry) {
        return res.status(404).json({ message: "No active timer found" });
      }
      res.json(timeEntry);
    } catch (error) {
      res.status(500).json({ message: "Failed to stop timer" });
    }
  });

  app.get("/api/tasks/:taskId/time-entries", async (req, res) => {
    try {
      const entries = await storage.getTimeEntriesByTask(req.params.taskId);
      res.json(entries);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch time entries" });
    }
  });

  // Feature Flags
  app.get("/api/feature-flags", async (req, res) => {
    try {
      const { userId = 'default-user', workspaceId } = req.query;
      const flags = await featureFlagService.getFeatureFlags(
        userId as string,
        workspaceId as string | undefined
      );
      res.json(flags);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch feature flags" });
    }
  });

  app.post("/api/feature-flags", async (req, res) => {
    try {
      const { scopeType, scopeId, key, value } = req.body;

      if (!['user', 'workspace'].includes(scopeType)) {
        return res.status(400).json({ message: "Invalid scope type" });
      }

      await featureFlagService.setFeatureFlag(scopeType, scopeId, key, value);
      res.status(201).json({ success: true });
    } catch (error) {
      res.status(500).json({ message: "Failed to set feature flag" });
    }
  });

  // Enhanced Tasks API for Kanban
  app.get("/api/tasks", async (req, res) => {
    try {
      const { projectId, status, assigneeId } = req.query;
      const tasks = await storage.getTasks({
        projectId: projectId as string,
        status: status as string,
        assigneeId: assigneeId as string,
      });
      res.json(tasks);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch tasks" });
    }
  });

  app.post("/api/tasks", async (req, res) => {
    try {
      const validation = insertTaskSchema.safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid task data", errors: validation.error.errors });
      }

      const task = await storage.createTask(validation.data);
      res.status(201).json(task);
    } catch (error) {
      res.status(500).json({ message: "Failed to create task" });
    }
  });

  // Spaces and Workspaces (basic support)
  app.get("/api/spaces", async (req, res) => {
    try {
      // For now, return a simple personal/professional space structure
      const spaces = [
        { id: 'personal', type: 'personal', name: 'Personal' },
        { id: 'professional', type: 'professional', name: 'Professional' }
      ];
      res.json(spaces);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch spaces" });
    }
  });

  app.get("/api/workspaces", async (req, res) => {
    try {
      const { spaceId } = req.query;
      // For now, return empty workspaces - this would be expanded in full implementation
      const workspaces = [];
      res.json(workspaces);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch workspaces" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  type Project,
  type InsertProject,
  type Note,
  type InsertNote,
  type Task,
  type InsertTask,
  type TimeEntry,
  type InsertTimeEntry,
  type ActiveTimer,
  type InsertActiveTimer,
  type ProjectWithStats,
  type TaskWithTimeTracking,
  type NoteWithTaskCount,
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Projects
  getProjects(): Promise<ProjectWithStats[]>;
  getProject(id: string): Promise<Project | undefined>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: string, project: Partial<InsertProject>): Promise<Project | undefined>;
  deleteProject(id: string): Promise<boolean>;

  // Notes
  getNotesByProject(projectId: string): Promise<NoteWithTaskCount[]>;
  getNote(id: string): Promise<Note | undefined>;
  createNote(note: InsertNote): Promise<Note>;
  updateNote(id: string, note: Partial<InsertNote>): Promise<Note | undefined>;
  deleteNote(id: string): Promise<boolean>;

  // Tasks
  getTasksByProject(projectId: string): Promise<TaskWithTimeTracking[]>;
  getTask(id: string): Promise<Task | undefined>;
  createTask(task: InsertTask): Promise<Task>;
  updateTask(id: string, task: Partial<InsertTask>): Promise<Task | undefined>;
  deleteTask(id: string): Promise<boolean>;

  // Time Entries
  getTimeEntriesByTask(taskId: string): Promise<TimeEntry[]>;
  createTimeEntry(entry: InsertTimeEntry): Promise<TimeEntry>;
  updateTimeEntry(id: string, entry: Partial<InsertTimeEntry>): Promise<TimeEntry | undefined>;

  // Active Timers
  getActiveTimer(): Promise<ActiveTimer | undefined>;
  startTimer(timer: InsertActiveTimer): Promise<ActiveTimer>;
  stopTimer(): Promise<TimeEntry | undefined>;
}

export class MemStorage implements IStorage {
  private projects: Map<string, Project> = new Map();
  private notes: Map<string, Note> = new Map();
  private tasks: Map<string, Task> = new Map();
  private timeEntries: Map<string, TimeEntry> = new Map();
  private activeTimers: Map<string, ActiveTimer> = new Map();

  constructor() {
    this.seedData();
  }

  private seedData() {
    // Create default project
    const defaultProject: Project = {
      id: "project-1",
      name: "Marketing Campaign",
      description: "Q4 marketing campaign planning and execution",
      color: "#6366F1",
      spaceId: null,
      workspaceId: null,
      status: "active",
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.projects.set(defaultProject.id, defaultProject);
  }

  // Projects
  async getProjects(): Promise<ProjectWithStats[]> {
    const projects = Array.from(this.projects.values());
    return projects.map(project => {
      const projectTasks = Array.from(this.tasks.values()).filter(t => t.projectId === project.id);
      const projectNotes = Array.from(this.notes.values()).filter(n => n.projectId === project.id);
      return {
        ...project,
        taskCount: projectTasks.length,
        noteCount: projectNotes.length,
      };
    });
  }

  async getProject(id: string): Promise<Project | undefined> {
    return this.projects.get(id);
  }

  async createProject(insertProject: InsertProject): Promise<Project> {
    const id = randomUUID();
    const project: Project = {
      ...insertProject,
      id,
      color: insertProject.color || "#6366F1",
      description: insertProject.description || null,
      spaceId: insertProject.spaceId || null,
      workspaceId: insertProject.workspaceId || null,
      status: insertProject.status || "active",
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.projects.set(id, project);
    return project;
  }

  async updateProject(id: string, update: Partial<InsertProject>): Promise<Project | undefined> {
    const project = this.projects.get(id);
    if (!project) return undefined;

    const updatedProject: Project = { 
      ...project, 
      ...update, 
      updatedAt: new Date() 
    };
    this.projects.set(id, updatedProject);
    return updatedProject;
  }

  async deleteProject(id: string): Promise<boolean> {
    return this.projects.delete(id);
  }

  // Notes
  async getNotesByProject(projectId: string): Promise<NoteWithTaskCount[]> {
    const notes = Array.from(this.notes.values()).filter(n => n.projectId === projectId);
    return notes.map(note => {
      const tasksFromNote = Array.from(this.tasks.values()).filter(t => t.noteId === note.id);
      return {
        ...note,
        tasksExtracted: tasksFromNote.length,
      };
    });
  }

  async getNote(id: string): Promise<Note | undefined> {
    return this.notes.get(id);
  }

  async createNote(insertNote: InsertNote): Promise<Note> {
    const id = randomUUID();
    const note: Note = {
      ...insertNote,
      id,
      content: insertNote.content || "",
      spaceId: insertNote.spaceId || null,
      workspaceId: insertNote.workspaceId || null,
      projectId: insertNote.projectId || null,
      authorId: insertNote.authorId || null,
      tags: (insertNote.tags as string[]) || [],
      backlinks: (insertNote.backlinks as string[]) || [],
      visibilityScope: insertNote.visibilityScope || "private",
      lastProcessedLength: insertNote.lastProcessedLength || 0,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.notes.set(id, note);
    return note;
  }

  async updateNote(id: string, update: Partial<InsertNote>): Promise<Note | undefined> {
    const note = this.notes.get(id);
    if (!note) return undefined;

    const updatedNote = { 
      ...note, 
      ...update, 
      tags: (update.tags as string[]) || note.tags,
      backlinks: (update.backlinks as string[]) || note.backlinks,
      updatedAt: new Date() 
    };
    this.notes.set(id, updatedNote);
    return updatedNote;
  }

  async deleteNote(id: string): Promise<boolean> {
    return this.notes.delete(id);
  }

  // Tasks
  async getTasksByProject(projectId: string): Promise<TaskWithTimeTracking[]> {
    const tasks = Array.from(this.tasks.values()).filter(t => t.projectId === projectId);
    return tasks.map(task => {
      const taskTimeEntries = Array.from(this.timeEntries.values()).filter(te => te.taskId === task.id);
      const totalTime = taskTimeEntries.reduce((sum, entry) => sum + entry.duration, 0);
      const activeTimer = Array.from(this.activeTimers.values()).find(at => at.taskId === task.id);

      return {
        ...task,
        totalTime,
        isActive: !!activeTimer,
      };
    });
  }

  async getTask(id: string): Promise<Task | undefined> {
    return this.tasks.get(id);
  }

  // Enhanced task queries with filtering
  async getTasks(filters?: { 
    projectId?: string; 
    status?: string; 
    assigneeId?: string; 
  }): Promise<Task[]> {
    let tasks = Array.from(this.tasks.values());

    if (filters?.projectId) {
      tasks = tasks.filter(task => task.projectId === filters.projectId);
    }
    if (filters?.status) {
      tasks = tasks.filter(task => task.status === filters.status);
    }
    if (filters?.assigneeId) {
      tasks = tasks.filter(task => task.assigneeId === filters.assigneeId);
    }

    return tasks;
  }

  async createTask(insertTask: InsertTask): Promise<Task> {
    const id = randomUUID();
    const task: Task = {
      ...insertTask,
      id,
      description: insertTask.description || null,
      priority: insertTask.priority || "medium",
      status: insertTask.status || "todo",
      noteId: insertTask.noteId || null,
      assigneeId: insertTask.assigneeId || null,
      dueDate: insertTask.dueDate || null,
      tags: (insertTask.tags as string[]) || [],
      seriesId: insertTask.seriesId || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.tasks.set(id, task);
    return task;
  }

  async updateTask(id: string, update: Partial<InsertTask>): Promise<Task | undefined> {
    const task = this.tasks.get(id);
    if (!task) return undefined;

    const updatedTask = { 
      ...task, 
      ...update, 
      tags: (update.tags as string[]) || task.tags,
      updatedAt: new Date() 
    };
    this.tasks.set(id, updatedTask);
    return updatedTask;
  }

  async deleteTask(id: string): Promise<boolean> {
    return this.tasks.delete(id);
  }

  // Time Entries
  async getTimeEntriesByTask(taskId: string): Promise<TimeEntry[]> {
    return Array.from(this.timeEntries.values()).filter(te => te.taskId === taskId);
  }

  async createTimeEntry(insertEntry: InsertTimeEntry): Promise<TimeEntry> {
    const id = randomUUID();
    const entry: TimeEntry = {
      ...insertEntry,
      id,
      duration: insertEntry.duration || 0,
      description: insertEntry.description || null,
      endTime: insertEntry.endTime || null,
      createdAt: new Date(),
    };
    this.timeEntries.set(id, entry);
    return entry;
  }

  async updateTimeEntry(id: string, update: Partial<InsertTimeEntry>): Promise<TimeEntry | undefined> {
    const entry = this.timeEntries.get(id);
    if (!entry) return undefined;

    const updatedEntry = { ...entry, ...update };
    this.timeEntries.set(id, updatedEntry);
    return updatedEntry;
  }

  // Active Timers - Support multiple concurrent timers
  async getActiveTimer(): Promise<ActiveTimer | undefined> {
    const timers = Array.from(this.activeTimers.values());
    return timers[0]; // For backward compatibility, return first
  }

  async getActiveTimers(): Promise<ActiveTimer[]> {
    return Array.from(this.activeTimers.values());
  }

  async getActiveTimerByTask(taskId: string): Promise<ActiveTimer | undefined> {
    return Array.from(this.activeTimers.values()).find(timer => timer.taskId === taskId);
  }

  async startTimer(insertTimer: InsertActiveTimer): Promise<ActiveTimer> {
    // Check if this task already has an active timer
    const existingTimer = await this.getActiveTimerByTask(insertTimer.taskId);
    if (existingTimer) {
      return existingTimer; // Return existing timer if task is already being tracked
    }

    const id = randomUUID();
    const timer: ActiveTimer = {
      ...insertTimer,
      id,
      createdAt: new Date(),
    };
    this.activeTimers.set(id, timer);
    return timer;
  }

  async stopTimer(taskId?: string): Promise<TimeEntry | undefined> {
    let activeTimer: ActiveTimer | undefined;

    if (taskId) {
      // Stop timer for specific task
      activeTimer = await this.getActiveTimerByTask(taskId);
    } else {
      // Stop any active timer (backward compatibility)
      activeTimer = await this.getActiveTimer();
    }

    if (!activeTimer) return undefined;

    const endTime = new Date();
    const duration = Math.floor((endTime.getTime() - activeTimer.startTime.getTime()) / 1000);

    const timeEntry = await this.createTimeEntry({
      taskId: activeTimer.taskId,
      startTime: activeTimer.startTime,
      endTime,
      duration,
    });

    this.activeTimers.delete(activeTimer.id);
    return timeEntry;
  }
}

export const storage = new MemStorage();

