  .ProseMirror h1,
  .ProseMirror h2,
  .ProseMirror h3,
  .ProseMirror h4,
  .ProseMirror h5,
  .ProseMirror h6 {
    @apply font-semibold text-foreground;
  }

  .ProseMirror h1 {
    @apply text-3xl mb-4 mt-6;
  }

  .ProseMirror h2 {
    @apply text-2xl mb-3 mt-5;
  }

  .ProseMirror h3 {
    @apply text-xl mb-2 mt-4;
  }

  .ProseMirror p {
    @apply mb-3 leading-relaxed;
  }

  .ProseMirror ul,
  .ProseMirror ol {
    @apply mb-3 pl-6;
  }

  .ProseMirror li {
    @apply mb-1;
  }

  .ProseMirror ul li {
    @apply list-disc;
  }

  .ProseMirror ol li {
    @apply list-decimal;
  }

  .ProseMirror blockquote {
    @apply border-l-4 border-primary pl-4 my-4 italic text-muted-foreground;
  }

  .ProseMirror code {
    @apply bg-muted px-1 py-0.5 rounded text-sm font-mono;
  }

  .ProseMirror pre {
    @apply bg-muted p-4 rounded mb-3 overflow-x-auto;
  }

  .ProseMirror pre code {
    @apply bg-transparent p-0;
  }

  .ProseMirror mark {
    @apply bg-yellow-200 dark:bg-yellow-800 px-1 rounded;
  }

  .ProseMirror strong {
    @apply font-semibold;
  }

  .ProseMirror em {
    @apply italic;
  }

  .ProseMirror a {
    @apply text-primary underline hover:text-primary hover:opacity-80;
  }

  /* Drag and drop styles */
  .task-card-dragging {
    @apply opacity-50 rotate-2 shadow-lg;
  }

  .drop-zone-active {
    @apply bg-primary bg-opacity-5 border-primary border-opacity-30 border-2 border-dashed;
  }

  /* Custom scrollbar */
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: transparent;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(var(--muted-foreground));
    opacity: 0.3;
    border-radius: 3px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--muted-foreground));
    opacity: 0.5;
  }
}

/* Animation classes */
@keyframes pulse-ai {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.animate-pulse-ai {
  animation: pulse-ai 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Priority badge colors */
.priority-low {
  @apply bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200;
}

.priority-medium {
  @apply bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200;
}

.priority-high {
  @apply bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200;
}

.priority-urgent {
  @apply bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200;
}

/* Status badge colors */
.status-todo {
  @apply bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-200;
}

.status-in-progress {
  @apply bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200;
}

.status-done {
  @apply bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200;
}

