import { GoogleGenAI } from "@google/genai";

// Make Gemini optional - only initialize if API key is provided
const gemini = process.env.GEMINI_API_KEY 
  ? new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY })
  : null;

export interface ExtractedTask {
  title: string;
  description: string;
  priority: 'low' | 'medium' | 'high' | 'urgent';
  dueDate?: string;
}

// Simple fallback task extraction without AI
function extractTasksWithoutAI(content: string): ExtractedTask[] {
  const tasks: ExtractedTask[] = [];

  // Simple patterns to identify potential tasks
  const taskPatterns = [
    /(?:^|\n)\s*[-*]\s+(.+?)(?:\n|$)/g, // Bullet points
    /(?:^|\n)\s*\d+\.\s+(.+?)(?:\n|$)/g, // Numbered lists
    /(?:need to|should|must|have to|will|going to|plan to|todo:?)\s+(.+?)(?:\.|$)/gi, // Action words
    /(?:^|\n)\s*(?:TODO:?|TASK:?|ACTION:?)\s*(.+?)(?:\n|$)/gi, // Explicit task markers
  ];

  taskPatterns.forEach(pattern => {
    let match;
    while ((match = pattern.exec(content)) !== null) {
      const taskText = match[1].trim();
      if (taskText.length > 3 && taskText.length < 200 && 
          !taskText.endsWith(':') && 
          !taskText.toLowerCase().includes('action items') &&
          !taskText.toLowerCase().includes('additional tasks') &&
          !taskText.toLowerCase().includes('meeting notes') &&
          !/^(here are|these are|following)/i.test(taskText)) {
        // Simple priority detection based on keywords
        let priority: 'low' | 'medium' | 'high' | 'urgent' = 'medium';
        if (taskText.toLowerCase().includes('urgent') || taskText.toLowerCase().includes('asap')) {
          priority = 'urgent';
        } else if (taskText.toLowerCase().includes('important') || taskText.toLowerCase().includes('critical')) {
          priority = 'high';
        } else if (taskText.toLowerCase().includes('later') || taskText.toLowerCase().includes('eventually')) {
          priority = 'low';
        }

        // Simple due date extraction
        const dateMatch = taskText.match(/(?:by|due|before|until)\s+(\w+\s+\d{1,2}(?:st|nd|rd|th)?,?\s+\d{4}|\d{1,2}\/\d{1,2}\/\d{4}|\d{4}-\d{2}-\d{2})/i);
        let dueDate: string | undefined;
        if (dateMatch) {
          // For now, just set a future date - could be enhanced with proper date parsing
          const futureDate = new Date();
          futureDate.setDate(futureDate.getDate() + 7);
          dueDate = futureDate.toISOString().split('T')[0];
        }

        tasks.push({
          title: taskText.replace(/^(TODO:?|TASK:?|ACTION:?)\s*/i, '').trim(),
          description: `Extracted from note content`,
          priority,
          dueDate
        });
      }
    }
  });

  return tasks;
}

export async function extractTasksFromNote(content: string): Promise<ExtractedTask[]> {
  if (!gemini) {
    console.log('Using fallback task extraction (no Gemini API key)');
    return extractTasksWithoutAI(content);
  }

  try {
    const systemPrompt = `You are an expert at analyzing text and extracting actionable tasks. Look for:
- Action verbs (schedule, create, review, meet, call, etc.)
- Deadlines or time references  
- Specific deliverables or outcomes
- Meeting requests or appointments

For each task found, determine:
- A clear, specific title
- A brief description
- Priority level (low, medium, high, urgent) based on context and deadlines
- Due date if mentioned (format as YYYY-MM-DD)

Only extract genuine, actionable items that require specific action or follow-up.
Return JSON in this format: { "tasks": [{ "title": "Task title", "description": "Task description", "priority": "medium", "dueDate": "2024-11-01" }] }`;

    const prompt = `Analyze this note content and extract actionable tasks:\n\n${content}`;

    const response = await gemini.models.generateContent({
      model: "gemini-2.5-pro",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: "object",
          properties: {
            tasks: {
              type: "array",
              items: {
                type: "object",
                properties: {
                  title: { type: "string" },
                  description: { type: "string" },
                  priority: { type: "string", enum: ["low", "medium", "high", "urgent"] },
                  dueDate: { type: "string" }
                },
                required: ["title", "description", "priority"]
              }
            }
          },
          required: ["tasks"]
        },
        temperature: 0.3,
      },
      contents: prompt,
    });

    const rawJson = response.text;
    if (rawJson) {
      const result = JSON.parse(rawJson);
      return result.tasks || [];
    } else {
      throw new Error("Empty response from Gemini model");
    }
  } catch (error) {
    console.error('Error extracting tasks from note with Gemini:', error);
    // Fall back to pattern matching if AI fails
    console.log('Falling back to pattern-based task extraction');
    return extractTasksWithoutAI(content);
  }
}

export async function generateTaskSuggestions(noteTitle: string, noteContent: string): Promise<string[]> {
  if (!gemini) {
    console.warn('Gemini API key not provided, skipping task suggestions');
    return [];
  }

  try {
    const systemPrompt = `You are a helpful project management assistant that suggests logical next steps based on note content. Focus on actionable items that would help move the project forward.

Return JSON in this format: { "suggestions": ["suggestion 1", "suggestion 2", "suggestion 3"] }`;

    const prompt = `Based on this note title "${noteTitle}" and content, suggest 3-5 relevant next steps or related tasks:\n\n${noteContent}`;

    const response = await gemini.models.generateContent({
      model: "gemini-2.5-pro",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: "object",
          properties: {
            suggestions: {
              type: "array",
              items: { type: "string" }
            }
          },
          required: ["suggestions"]
        },
        temperature: 0.7,
      },
      contents: prompt,
    });

    const rawJson = response.text;
    if (rawJson) {
      const result = JSON.parse(rawJson);
      return result.suggestions || [];
    } else {
      throw new Error("Empty response from Gemini model");
    }
  } catch (error) {
    console.error('Error generating task suggestions with Gemini:', error);
    return [];
  }
}

client/src/components/kanban-board.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { DragDropContext, Droppable, Draggable, DropResult } from "@hello-pangea/dnd";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertTaskSchema } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { Plus, Play, Pause, MoreHorizontal, Link, Calendar } from "lucide-react";
import { formatTime } from "@/lib/time-utils";
import type { TaskWithTimeTracking, InsertTask, ActiveTimer } from "@shared/schema";
import { cn } from "@/lib/utils";

interface KanbanBoardProps {
  projectId: string;
}

const taskStatuses = [
  { id: 'todo', title: 'To Do', color: 'bg-gray-400' },
  { id: 'in_progress', title: 'In Progress', color: 'bg-ai-primary' },
  { id: 'done', title: 'Done', color: 'bg-accent' },
] as const;

const priorityColors = {
  low: 'priority-low',
  medium: 'priority-medium', 
  high: 'priority-high',
  urgent: 'priority-urgent'
};

export default function KanbanBoard({ projectId }: KanbanBoardProps) {
  const [newTaskOpen, setNewTaskOpen] = useState(false);
  const { toast } = useToast();

  const { data: tasks = [] } = useQuery<TaskWithTimeTracking[]>({
    queryKey: ['/api/projects', projectId, 'tasks'],
    enabled: !!projectId,
  });

  const { data: activeTimer } = useQuery<ActiveTimer | null>({
    queryKey: ['/api/timer/active'],
    refetchInterval: 1000, // Update every second
  });

  const { data: activeTimers = [] } = useQuery<ActiveTimer[]>({
    queryKey: ['/api/timers/active'],
    refetchInterval: 1000, // Update every second
  });

  const form = useForm<InsertTask>({
    resolver: zodResolver(insertTaskSchema),
    defaultValues: {
      projectId: projectId,
      title: "",
      description: "",
      status: "todo",
      priority: "medium",
    },
  });

  const createTaskMutation = useMutation({
    mutationFn: async (data: InsertTask) => {
      const response = await apiRequest('POST', `/api/projects/${projectId}/tasks`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
      toast({
        title: "Task created",
        description: "Your new task has been created successfully.",
      });
      setNewTaskOpen(false);
      form.reset();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create task. Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateTaskMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string, data: Partial<InsertTask> }) => {
      const response = await apiRequest('PATCH', `/api/tasks/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
    },
  });

  const startTimerMutation = useMutation({
    mutationFn: async (taskId: string) => {
      const response = await apiRequest('POST', '/api/timer/start', {
        taskId,
        startTime: new Date(),
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/timer/active'] });
      queryClient.invalidateQueries({ queryKey: ['/api/timers/active'] });
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
      toast({
        title: "Timer started",
        description: "Time tracking started automatically.",
      });
    },
  });

  const stopTimerMutation = useMutation({
    mutationFn: async (taskId?: string) => {
      const response = await apiRequest('POST', '/api/timer/stop', { taskId });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/timer/active'] });
      queryClient.invalidateQueries({ queryKey: ['/api/timers/active'] });
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
      toast({
        title: "Timer stopped",
        description: "Time logged automatically.",
      });
    },
  });

  const onSubmit = (data: InsertTask) => {
    createTaskMutation.mutate({ ...data, projectId });
  };

  const handleDragEnd = async (result: DropResult) => {
    if (!result.destination) return;

    const taskId = result.draggableId;
    const oldStatus = result.source.droppableId as 'todo' | 'in_progress' | 'done';
    const newStatus = result.destination.droppableId as 'todo' | 'in_progress' | 'done';

    // Update task status
    updateTaskMutation.mutate({
      id: taskId,
      data: { status: newStatus }
    });

    // Auto-start timer when moving to "in_progress"
    if (newStatus === 'in_progress' && oldStatus !== 'in_progress') {
      // Check if this task already has an active timer
      const taskHasActiveTimer = activeTimers.some(timer => timer.taskId === taskId);
      if (!taskHasActiveTimer) {
        startTimerMutation.mutate(taskId);
      }
    }

    // Auto-stop timer when moving to "done" or back to "todo"
    if (oldStatus === 'in_progress' && newStatus !== 'in_progress') {
      // Find and stop the timer for this specific task
      const taskActiveTimer = activeTimers.find(timer => timer.taskId === taskId);
      if (taskActiveTimer) {
        stopTimerMutation.mutate(taskId);
      }
    }
  };

  const handleStartTimer = (taskId: string) => {
    startTimerMutation.mutate(taskId);
  };

  const handleStopTimer = (taskId?: string) => {
    stopTimerMutation.mutate(taskId);
  };

  const getTasksByStatus = (status: string) => {
    return tasks.filter(task => task.status === status);
  };

  return (
    <div className="h-full p-6 overflow-x-auto">
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="flex space-x-6 h-full min-w-max">
          {taskStatuses.map((status) => {
            const statusTasks = getTasksByStatus(status.id);

            return (
              <div key={status.id} className="flex-1 bg-ai-card rounded-xl border border-border flex flex-col min-h-0 min-w-80">
                <div className="p-4 border-b border-border flex items-center justify-between">
                  <div className="flex items-center">
                    <div className={cn("w-3 h-3 rounded-full mr-3", status.color)} />
                    <h3 className="font-semibold text-ai-text">{status.title}</h3>
                    <Badge variant="secondary" className="ml-2">
                      {statusTasks.length}
                    </Badge>
                  </div>
                  <Dialog open={newTaskOpen} onOpenChange={setNewTaskOpen}>
                    <DialogTrigger asChild>
                      <Button size="sm" variant="ghost" data-testid={`add-task-${status.id}`}>
                        <Plus className="h-4 w-4 text-muted-foreground" />
                      </Button>
                    </DialogTrigger>
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Create New Task</DialogTitle>
                        <DialogDescription>
                          Add a new task to your project.
                        </DialogDescription>
                      </DialogHeader>
                      <Form {...form}>
                        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                          <FormField
                            control={form.control}
                            name="title"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Task Title</FormLabel>
                                <FormControl>
                                  <Input 
                                    placeholder="Enter task title" 
                                    data-testid="input-task-title"
                                    {...field} 
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="description"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Description</FormLabel>
                                <FormControl>
                                  <Textarea 
                                    placeholder="Enter task description"
                                    data-testid="input-task-description"
                                    {...field}
                                    value={field.value || ""}
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="priority"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Priority</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                  <FormControl>
                                    <SelectTrigger data-testid="select-task-priority">
                                      <SelectValue placeholder="Select priority" />
                                    </SelectTrigger>
                                  </FormControl>
                                  <SelectContent>
                                    <SelectItem value="low">Low Priority</SelectItem>
                                    <SelectItem value="medium">Medium Priority</SelectItem>
                                    <SelectItem value="high">High Priority</SelectItem>
                                    <SelectItem value="urgent">Urgent</SelectItem>
                                  </SelectContent>
                                </Select>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="status"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Status</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                  <FormControl>
                                    <SelectTrigger data-testid="select-task-status">
                                      <SelectValue placeholder="Select status" />
                                    </SelectTrigger>
                                  </FormControl>
                                  <SelectContent>
                                    <SelectItem value="todo">To Do</SelectItem>
                                    <SelectItem value="in_progress">In Progress</SelectItem>
                                    <SelectItem value="done">Done</SelectItem>
                                  </SelectContent>
                                </Select>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <div className="flex justify-end space-x-2">
                            <Button 
                              type="button" 
                              variant="outline" 
                              onClick={() => setNewTaskOpen(false)}
                              data-testid="cancel-task"
                            >
                              Cancel
                            </Button>
                            <Button 
                              type="submit" 
                              disabled={createTaskMutation.isPending}
                              data-testid="submit-task"
                            >
                              {createTaskMutation.isPending ? "Creating..." : "Create Task"}
                            </Button>
                          </div>
                        </form>
                      </Form>
                    </DialogContent>
                  </Dialog>
                </div>

                <Droppable droppableId={status.id}>
                  {(provided, snapshot) => (
                    <div 
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className={cn(
                        "flex-1 p-4 space-y-3 overflow-y-auto custom-scrollbar",
                        snapshot.isDraggingOver && "drop-zone-active"
                      )}
                    >
                      {statusTasks.length === 0 ? (
                        <div className="text-center py-8">
                          <p className="text-muted-foreground text-sm">No tasks yet</p>
                          <p className="text-muted-foreground text-xs mt-1">Drag tasks here or create new ones</p>
                        </div>
                      ) : (
                        statusTasks.map((task, index) => (
                          <Draggable key={task.id} draggableId={task.id} index={index}>
                            {(provided, snapshot) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                className={cn(
                                  "bg-white dark:bg-ai-card border border-border rounded-lg p-4 shadow-sm hover:shadow-md transition-shadow cursor-pointer",
                                  activeTimers.some(timer => timer.taskId === task.id) && "border-2 border-ai-primary/50",
                                  snapshot.isDragging && "task-card-dragging"
                                )}
                                data-testid={`task-${task.id}`}
                              >
                                <div className="flex items-start justify-between mb-3">
                                  <h4 className="font-medium text-ai-text flex-1 line-clamp-2">
                                    {task.title}
                                  </h4>
                                  <div className="flex items-center space-x-2 ml-2">
                                    {activeTimers.some(timer => timer.taskId === task.id) ? (
                                      <div className="flex items-center space-x-1">
                                        <div className="flex items-center space-x-1 bg-red-100 dark:bg-red-900 px-2 py-1 rounded">
                                          <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse" />
                                          <span className="text-xs font-medium text-red-600 dark:text-red-400">
                                            Active
                                          </span>
                                        </div>
                                        <Button
                                          size="sm"
                                          variant="ghost"
                                          onClick={() => handleStopTimer(task.id)}
                                          className="p-1 h-6 w-6 bg-red-100 hover:bg-red-200 dark:bg-red-900 dark:hover:bg-red-800"
                                          data-testid={`stop-timer-${task.id}`}
                                        >
                                          <Pause className="h-3 w-3 text-red-600" />
                                        </Button>
                                      </div>
                                    ) : (
                                      <Button
                                        size="sm"
                                        variant="ghost"
                                        onClick={() => handleStartTimer(task.id)}
                                        className="p-1 h-6 w-6 hover:bg-accent/20"
                                        data-testid={`start-timer-${task.id}`}
                                      >
                                        <Play className="h-3 w-3 text-accent" />
                                      </Button>
                                    )}
                                    <Button
                                      size="sm"
                                      variant="ghost"
                                      className="p-1 h-6 w-6"
                                      data-testid={`task-menu-${task.id}`}
                                    >
                                      <MoreHorizontal className="h-3 w-3 text-muted-foreground" />
                                    </Button>
                                  </div>
                                </div>

                                {task.description && (
                                  <p className="text-sm text-muted-foreground mb-3 line-clamp-2">
                                    {task.description}
                                  </p>
                                )}

                                <div className="flex items-center justify-between">
                                  <div className="flex items-center space-x-2">
                                    <Badge 
                                      variant="secondary" 
                                      className={cn("text-xs", priorityColors[task.priority as keyof typeof priorityColors])}
                                    >
                                      {task.priority.charAt(0).toUpperCase() + task.priority.slice(1)} Priority
                                    </Badge>
                                    {task.noteId && (
                                      <Button
                                        size="sm"
                                        variant="ghost"
                                        className="text-xs text-ai-secondary hover:text-ai-secondary/80 p-0 h-auto"
                                        data-testid={`note-link-${task.id}`}
                                      >
                                        <Link className="h-3 w-3 mr-1" />
                                        From note
                                      </Button>
                                    )}
                                  </div>

                                  <div className="text-xs text-muted-foreground">
                                    {task.totalTime > 0 && (
                                      <span className="text-accent">
                                        Total: {formatTime(task.totalTime)}
                                      </span>
                                    )}
                                    {task.dueDate && (
                                      <span className="ml-2">
                                        <Calendar className="h-3 w-3 inline mr-1" />
                                        Due {new Date(task.dueDate).toLocaleDateString()}
                                      </span>
                                    )}
                                  </div>
                                </div>
                              </div>
                            )}
                          </Draggable>
                        ))
                      )}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </div>
            );
          })}
        </div>
      </DragDropContext>
    </div>
  );
}

client/src/components/notes-editor.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertNoteSchema } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import RichTextEditor from "@/components/ui/rich-text-editor";
import { Search, Plus, Share, Bot, Lightbulb, Link, Sparkles } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import type { NoteWithTaskCount, InsertNote } from "@shared/schema";
import { cn } from "@/lib/utils";
import { formatDistanceToNow } from "date-fns";

interface NotesEditorProps {
  projectId: string;
}

export default function NotesEditor({ projectId }: NotesEditorProps) {
  const [selectedNote, setSelectedNote] = useState<string | null>(null);
  const [newNoteOpen, setNewNoteOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [extractingTasks, setExtractingTasks] = useState(false);
  const { toast } = useToast();

  const { data: notes = [] } = useQuery<NoteWithTaskCount[]>({
    queryKey: ['/api/projects', projectId, 'notes'],
    enabled: !!projectId,
  });

  const { data: currentNote } = useQuery({
    queryKey: ['/api/notes', selectedNote],
    enabled: !!selectedNote,
  });

  const form = useForm<InsertNote>({
    resolver: zodResolver(insertNoteSchema),
    defaultValues: {
      projectId: projectId,
      title: "",
      content: "",
    },
  });

  const createNoteMutation = useMutation({
    mutationFn: async (data: InsertNote) => {
      const response = await apiRequest('POST', `/api/projects/${projectId}/notes`, data);
      return response.json();
    },
    onSuccess: async (note) => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'notes'] });

      // Auto-extract tasks from new note if it has content
      if (note.content && note.content.trim().length > 0) {
        try {
          await apiRequest('POST', `/api/notes/${note.id}/extract-tasks`);
          queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
          toast({
            title: "Note created & tasks extracted",
            description: "Your note has been created and tasks were automatically extracted.",
          });
        } catch (error) {
          toast({
            title: "Note created",
            description: "Your note has been created successfully.",
          });
        }
      } else {
        toast({
          title: "Note created",
          description: "Your new note has been created successfully.",
        });
      }

      setNewNoteOpen(false);
      form.reset();
      setSelectedNote(note.id);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create note. Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateNoteMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string, data: Partial<InsertNote> }) => {
      const response = await apiRequest('PATCH', `/api/notes/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'notes'] });
      queryClient.invalidateQueries({ queryKey: ['/api/notes', selectedNote] });
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
    },
  });

  const extractTasksMutation = useMutation({
    mutationFn: async (noteId: string) => {
      const response = await apiRequest('POST', `/api/notes/${noteId}/extract-tasks`);
      return response.json();
    },
    onSuccess: (result) => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'notes'] });
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
      toast({
        title: "Tasks extracted",
        description: `Successfully extracted ${result.count} tasks from your note.`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to extract tasks. Please try again.",
        variant: "destructive",
      });
    },
  });

  const filteredNotes = notes.filter(note =>
    note.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    note.content.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const onSubmit = (data: InsertNote) => {
    createNoteMutation.mutate({ ...data, projectId });
  };

  const handleNoteUpdate = (field: 'title' | 'content', value: string) => {
    if (!selectedNote) return;
    updateNoteMutation.mutate({
      id: selectedNote,
      data: { [field]: value }
    });
  };

  const handleExtractTasks = async () => {
    if (!selectedNote) return;
    setExtractingTasks(true);
    try {
      await extractTasksMutation.mutateAsync(selectedNote);
    } finally {
      setExtractingTasks(false);
    }
  };

  return (
    <div className="h-full flex">
      {/* Notes List */}
      <div className="w-80 bg-ai-card border-r border-border flex flex-col">
        <div className="p-4 border-b border-border">
          <div className="flex items-center justify-between mb-3">
            <h3 className="font-semibold text-ai-text">Notes</h3>
            <Dialog open={newNoteOpen} onOpenChange={setNewNoteOpen}>
              <DialogTrigger asChild>
                <Button size="sm" variant="ghost" data-testid="create-note">
                  <Plus className="h-4 w-4 text-ai-primary" />
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Create New Note</DialogTitle>
                  <DialogDescription>
                    Create a new note for your project.
                  </DialogDescription>
                </DialogHeader>
                <Form {...form}>
                  <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                    <FormField
                      control={form.control}
                      name="title"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Note Title</FormLabel>
                          <FormControl>
                            <Input 
                              placeholder="Enter note title" 
                              data-testid="input-note-title"
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <div className="flex justify-end space-x-2">
                      <Button 
                        type="button" 
                        variant="outline" 
                        onClick={() => setNewNoteOpen(false)}
                        data-testid="cancel-note"
                      >
                        Cancel
                      </Button>
                      <Button 
                        type="submit" 
                        disabled={createNoteMutation.isPending}
                        data-testid="submit-note"
                      >
                        {createNoteMutation.isPending ? "Creating..." : "Create Note"}
                      </Button>
                    </div>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
          </div>
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              type="text"
              placeholder="Search notes..."
              className="pl-10"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              data-testid="search-notes"
            />
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-2 custom-scrollbar">
          {filteredNotes.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-muted-foreground text-sm">No notes found</p>
              <p className="text-muted-foreground text-xs mt-1">
                {searchQuery ? "Try adjusting your search" : "Create your first note to get started"}
              </p>
            </div>
          ) : (
            filteredNotes.map((note) => (
              <button
                key={note.id}
                onClick={() => setSelectedNote(note.id)}
                className={cn(
                  "w-full p-3 rounded-lg cursor-pointer mb-2 text-left transition-colors",
                  selectedNote === note.id
                    ? "bg-ai-primary/10 border border-ai-primary/20"
                    : "hover:bg-muted"
                )}
                data-testid={`note-${note.id}`}
              >
                <div className="font-medium text-ai-text mb-1">{note.title}</div>
                <div className="text-sm text-muted-foreground mb-2 line-clamp-2">
                  {note.content.replace(/<[^>]*>/g, '').substring(0, 100)}...
                </div>
                <div className="flex items-center justify-between text-xs text-muted-foreground">
                  <span>{formatDistanceToNow(new Date(note.updatedAt), { addSuffix: true })}</span>
                  {note.tasksExtracted > 0 && (
                    <div className="flex items-center space-x-1">
                      <Bot className="h-3 w-3 text-ai-secondary" />
                      <span>{note.tasksExtracted} tasks</span>
                    </div>
                  )}
                </div>
              </button>
            ))
          )}
        </div>
      </div>

      {/* Rich Text Editor */}
      <div className="flex-1 flex flex-col">
        {selectedNote ? (
          <>
            {/* Editor Toolbar */}
            <div className="bg-ai-card border-b border-border p-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <Button 
                    size="sm" 
                    variant="outline"
                    onClick={handleExtractTasks}
                    disabled={extractingTasks || !currentNote}
                    className="bg-ai-secondary bg-opacity-10 text-ai-secondary hover:bg-ai-secondary hover:bg-opacity-20 border-ai-secondary border-opacity-30"
                    data-testid="extract-tasks"
                  >
                    {extractingTasks ? (
                      <>
                        <Sparkles className="h-4 w-4 mr-2 animate-spin" />
                        Extracting...
                      </>
                    ) : (
                      <>
                        <Bot className="h-4 w-4 mr-2" />
                        Extract Tasks
                      </>
                    )}
                  </Button>
                  <Button 
                    size="sm" 
                    variant="outline"
                    onClick={() => window.location.hash = '#tasks'}
                    data-testid="quick-create-task"
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Quick Task
                  </Button>
                  <Button size="sm" variant="ghost" data-testid="ai-suggestions">
                    <Lightbulb className="h-4 w-4 text-orange-500" />
                  </Button>
                </div>

                <div className="flex items-center space-x-2">
                  <span className="text-sm text-muted-foreground">Auto-saved</span>
                  <Button size="sm" className="bg-accent text-white hover:bg-accent/90" data-testid="share-note">
                    <Share className="h-4 w-4 mr-1" />
                    Share
                  </Button>
                </div>
              </div>
            </div>

            {/* Editor Content */}
            <div className="flex-1 overflow-y-auto custom-scrollbar">
              {currentNote ? (
                <div className="p-6 max-w-4xl mx-auto">
                  <Input
                    value={(currentNote as any)?.title || ""}
                    onChange={(e) => handleNoteUpdate('title', e.target.value)}
                    className="text-3xl font-bold border-none shadow-none p-0 mb-6 bg-transparent"
                    placeholder="Note title..."
                    data-testid="note-title-input"
                  />

                  <RichTextEditor
                    content={(currentNote as any)?.content || ""}
                    onChange={(content) => handleNoteUpdate('content', content)}
                    placeholder="Start writing your note..."
                  />
                </div>
              ) : (
                <div className="flex items-center justify-center h-full">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-ai-primary"></div>
                </div>
              )}
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center bg-muted/30">
            <div className="text-center">
              <div className="w-16 h-16 bg-ai-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <Plus className="h-8 w-8 text-ai-primary" />
              </div>
              <h3 className="text-lg font-semibold text-ai-text mb-2">No note selected</h3>
              <p className="text-muted-foreground mb-4">
                Select a note from the sidebar or create a new one
              </p>
              <Button 
                onClick={() => setNewNoteOpen(true)}
                className="bg-ai-primary text-white hover:bg-ai-primary/90"
                data-testid="create-first-note"
              >
                <Plus className="h-4 w-4 mr-2" />
                Create your first note
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/sidebar.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertProjectSchema } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { Plus, Settings, ChevronRight, PlusCircle, Clock, BarChart3 } from "lucide-react";
import type { ProjectWithStats, InsertProject } from "@shared/schema";
import { cn } from "@/lib/utils";

interface SidebarProps {
  isOpen: boolean;
  onProjectSelect: (projectId: string) => void;
  currentProjectId: string;
}

const projectColors = [
  '#6366F1', '#8B5CF6', '#10B981', '#F59E0B', '#EF4444', '#06B6D4', '#8B5CF6', '#F97316'
];

export default function Sidebar({ isOpen, onProjectSelect, currentProjectId }: SidebarProps) {
  const [newProjectOpen, setNewProjectOpen] = useState(false);
  const { toast } = useToast();

  const { data: projects = [] } = useQuery<ProjectWithStats[]>({
    queryKey: ['/api/projects'],
  });

  const form = useForm<InsertProject>({
    resolver: zodResolver(insertProjectSchema),
    defaultValues: {
      name: "",
      description: "",
      color: projectColors[0],
    },
  });

  const createProjectMutation = useMutation({
    mutationFn: async (data: InsertProject) => {
      const response = await apiRequest('POST', '/api/projects', data);
      return response.json();
    },
    onSuccess: (project) => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
      toast({
        title: "Project created",
        description: "Your new project has been created successfully.",
      });
      setNewProjectOpen(false);
      form.reset();
      onProjectSelect(project.id);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create project. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertProject) => {
    createProjectMutation.mutate(data);
  };

  if (!isOpen) {
    return (
      <div className="w-16 bg-ai-card border-r border-border flex flex-col items-center py-4">
        <Button variant="ghost" size="sm" className="mb-4">
          <Plus className="h-5 w-5" />
        </Button>
        {projects.map((project, index) => (
          <button
            key={project.id}
            onClick={() => onProjectSelect(project.id)}
            className={cn(
              "w-10 h-10 rounded-full mb-2 flex items-center justify-center text-white font-medium text-sm transition-colors",
              currentProjectId === project.id ? "ring-2 ring-primary" : ""
            )}
            style={{ backgroundColor: project.color }}
            data-testid={`project-${project.id}`}
          >
            {project.name.charAt(0).toUpperCase()}
          </button>
        ))}
      </div>
    );
  }

  return (
    <div className="w-80 bg-ai-card border-r border-border flex flex-col transition-all duration-300 shadow-sm">
      {/* Sidebar Header */}
      <div className="p-4 border-b border-border">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-xl font-semibold text-ai-text">AI Notes & Tasks</h1>
        </div>

        <Dialog open={newProjectOpen} onOpenChange={setNewProjectOpen}>
          <DialogTrigger asChild>
            <Button 
              className="w-full bg-ai-primary text-white hover:bg-ai-primary/90"
              data-testid="create-project"
            >
              <Plus className="h-4 w-4 mr-2" />
              New Project
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Project</DialogTitle>
              <DialogDescription>
                Create a new project to organize your notes and tasks.
              </DialogDescription>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Project Name</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="Enter project name" 
                          data-testid="input-project-name"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description (Optional)</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="Enter project description" 
                          data-testid="input-project-description"
                          {...field}
                          value={field.value || ""}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="color"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Color</FormLabel>
                      <FormControl>
                        <div className="flex space-x-2">
                          {projectColors.map((color) => (
                            <button
                              key={color}
                              type="button"
                              onClick={() => field.onChange(color)}
                              className={cn(
                                "w-8 h-8 rounded-full border-2 border-transparent",
                                field.value === color && "border-primary"
                              )}
                              style={{ backgroundColor: color }}
                              data-testid={`color-${color}`}
                            />
                          ))}
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setNewProjectOpen(false)}
                    data-testid="cancel-project"
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createProjectMutation.isPending}
                    data-testid="submit-project"
                  >
                    {createProjectMutation.isPending ? "Creating..." : "Create Project"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Project Navigation */}
      <div className="flex-1 overflow-y-auto p-4 custom-scrollbar">
        <div className="mb-6">
          <h3 className="text-sm font-medium text-muted-foreground mb-3 uppercase tracking-wider">
            Projects
          </h3>

          <div className="space-y-1">
            {projects.length === 0 ? (
              <div className="text-center py-8">
                <p className="text-muted-foreground text-sm">No projects yet</p>
                <p className="text-muted-foreground text-xs mt-1">Create your first project to get started</p>
              </div>
            ) : (
              projects.map((project) => (
                <button
                  key={project.id}
                  onClick={() => onProjectSelect(project.id)}
                  className={cn(
                    "w-full flex items-center p-3 rounded-lg cursor-pointer group transition-colors",
                    currentProjectId === project.id
                      ? "bg-ai-primary/10 border border-ai-primary/20"
                      : "hover:bg-muted"
                  )}
                  data-testid={`project-${project.id}`}
                >
                  <div 
                    className="w-3 h-3 rounded-full mr-3"
                    style={{ backgroundColor: project.color }}
                  />
                  <div className="flex-1 text-left">
                    <div className="font-medium text-ai-text">{project.name}</div>
                    <div className="text-sm text-muted-foreground">
                      {project.taskCount} tasks
                    </div>
                  </div>
                  <ChevronRight className={cn(
                    "h-4 w-4 transition-colors",
                    currentProjectId === project.id 
                      ? "text-ai-primary" 
                      : "text-muted-foreground group-hover:text-ai-primary"
                  )} />
                </button>
              ))
            )}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="mb-6">
          <h3 className="text-sm font-medium text-muted-foreground mb-3 uppercase tracking-wider">
            Quick Actions
          </h3>
          <div className="space-y-1">
            <Button 
              variant="ghost" 
              className="w-full justify-start"
              data-testid="quick-new-note"
            >
              <PlusCircle className="h-4 w-4 mr-3 text-accent" />
              <span className="font-medium">New Note</span>
            </Button>
            <Button 
              variant="ghost" 
              className="w-full justify-start"
              data-testid="quick-time-tracking"
            >
              <Clock className="h-4 w-4 mr-3 text-ai-secondary" />
              <span className="font-medium">Time Tracking</span>
            </Button>
            <Button 
              variant="ghost" 
              className="w-full justify-start"
              data-testid="quick-reports"
            >
              <BarChart3 className="h-4 w-4 mr-3 text-ai-primary" />
              <span className="font-medium">Reports</span>
            </Button>
          </div>
        </div>
      </div>

      {/* User Profile */}
      <div className="p-4 border-t border-border">
        <Button 
          variant="ghost" 
          className="w-full justify-start p-3"
          data-testid="user-profile"
        >
          <div className="w-8 h-8 bg-ai-primary rounded-full flex items-center justify-center text-white font-medium mr-3">
            JD
          </div>
          <div className="flex-1 text-left">
            <div className="font-medium text-ai-text">John Doe</div>
            <div className="text-sm text-muted-foreground">Pro Plan</div>
          </div>
          <Settings className="h-4 w-4 text-muted-foreground hover:text-ai-primary transition-colors" />
        </Button>
      </div>
    </div>
  );
}

client/src/components/time-tracking.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Clock, Calendar, TrendingUp, Play, OctagonMinus, Pause, Download, Edit } from "lucide-react";
import { formatTime, formatDuration } from "@/lib/time-utils";
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import { useState, useEffect } from "react";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface TimeTrackingProps {
  projectId: string;
}

export default function TimeTracking({ projectId }: TimeTrackingProps) {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [selectedPeriod, setSelectedPeriod] = useState("week");
  const { toast } = useToast();

  const { data: tasks = [] } = useQuery({
    queryKey: ['/api/projects', projectId, 'tasks'],
    enabled: !!projectId,
  });

  const { data: activeTimer } = useQuery({
    queryKey: ['/api/timer/active'],
    refetchInterval: 1000, // Update every second
  });

  const { data: activeTimers = [] } = useQuery({
    queryKey: ['/api/timers/active'],
    refetchInterval: 1000, // Update every second
  });

  // Update current time every second
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Calculate active timer duration
  const getActiveTimerDuration = () => {
    if (!activeTimer) return 0;
    return Math.floor((currentTime.getTime() - new Date((activeTimer as any)?.startTime).getTime()) / 1000);
  };

  // Calculate duration for specific timer
  const getTimerDuration = (timer: any) => {
    if (!timer) return 0;
    return Math.floor((currentTime.getTime() - new Date(timer.startTime).getTime()) / 1000);
  };

  // Get all active tasks
  const getActiveTasksWithTimers = () => {
    return (activeTimers as any[]).map((timer: any) => {
      const task = (tasks as any[]).find((task: any) => task.id === timer.taskId);
      return {
        timer,
        task,
        duration: getTimerDuration(timer)
      };
    });
  };

  // Get the active task (for backward compatibility)
  const activeTask = activeTimer ? (tasks as any[]).find((task: any) => task.id === (activeTimer as any)?.taskId) : null;

  const activeTasksWithTimers = getActiveTasksWithTimers();

  const stopTimerMutation = useMutation({
    mutationFn: async (taskId: string) => {
      const response = await apiRequest('POST', '/api/timer/stop', { taskId });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/timer/active'] });
      queryClient.invalidateQueries({ queryKey: ['/api/timers/active'] });
      queryClient.invalidateQueries({ queryKey: ['/api/projects', projectId, 'tasks'] });
      toast({
        title: "Timer stopped",
        description: "Time logged successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to stop timer. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Calculate time statistics
  const totalTimeToday = (tasks as any[]).reduce((sum: number, task: any) => sum + (task.totalTime || 0), 0);
  const totalTimeWeek = totalTimeToday; // Simplified for demo
  const averagePerDay = Math.floor(totalTimeWeek / 7);

  // Mock time entries for display
  const recentTimeEntries = [
    {
      id: '1',
      taskTitle: 'Define target audience',
      projectName: 'Marketing Campaign',
      date: 'Oct 24, 2024',
