import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Users, User } from "lucide-react";

type SpaceType = 'personal' | 'professional';

interface SpaceSwitcherProps {
  currentSpace?: SpaceType;
  onSpaceChange?: (space: SpaceType) => void;
  className?: string;
}

export default function SpaceSwitcher({ 
  currentSpace = 'personal', 
  onSpaceChange,
  className = "" 
}: SpaceSwitcherProps) {
  const [activeSpace, setActiveSpace] = useState<SpaceType>(currentSpace);

  const handleSpaceChange = (space: SpaceType) => {
    setActiveSpace(space);
    onSpaceChange?.(space);
  };

  return (
    <div className={`flex items-center space-x-1 ${className}`}>
      <Button
        variant={activeSpace === 'personal' ? 'default' : 'ghost'}
        size="sm"
        onClick={() => handleSpaceChange('personal')}
        className={`h-8 px-3 text-xs font-medium transition-colors ${
          activeSpace === 'personal' 
            ? "bg-ai-primary text-white hover:bg-ai-primary/90" 
            : "text-muted-foreground hover:bg-muted"
        }`}
      >
        <User className="h-3 w-3 mr-1.5" />
        Personal
      </Button>
      <Button
        variant={activeSpace === 'professional' ? 'default' : 'ghost'}
        size="sm"
        onClick={() => handleSpaceChange('professional')}
        className={`h-8 px-3 text-xs font-medium transition-colors ${
          activeSpace === 'professional' 
            ? "bg-ai-primary text-white hover:bg-ai-primary/90" 
            : "text-muted-foreground hover:bg-muted"
        }`}
      >
        <Users className="h-3 w-3 mr-1.5" />
        Professional
      </Button>
    </div>
  );
}
